import { createContext, useReducer } from 'react'

export const CartContext = createContext({
  isCartopen: true,
  setIsCartOpen: () => {},
  cartItems: [],
  decreaseItemInCart: () => {},
  addItemToCart: () => {},
  deleteItemInCart: () => {},
  cartCount: 0,
  cartTotal: 0,
})

const INITIAL_STATE = {
  isCartopen: false,
  cartItems: [],
  cartCount: 0,
  cartTotal: 0,
}
const cartReducer = (state, action) => {
  const { type, payload } = action

  switch (type) {
    case 'SET_CART_ITEMS':
      return {
        ...state,
        payload,
      }

    default:
      throw new Error(`Unhandled type of ${type} in CartReducer`)
  }
}
const addCartItems = (cartItems, productToAdd) => {
  const existingCarItem = cartItems.find(
    (cartItem) => cartItem.id === productToAdd.id
  )
  if (existingCarItem)
    return cartItems.map((cartItem) =>
      cartItem.id === productToAdd.id
        ? { ...cartItem, quantity: cartItem.quantity + 1 }
        : cartItem
    )

  return [...cartItems, { ...productToAdd, quantity: 1 }]
}
const decreaseCartItem = (cartItems, productToDecrese) => {
  const existingCarItem = cartItems.find(
    (cartItem) => cartItem.id === productToDecrese.id
  )
  if (existingCarItem.quantity === 1) {
    return cartItems.filter((cartItem) => cartItem.id !== productToDecrese.id)
  }
  return cartItems.map((cartItem) =>
    cartItem.id === productToDecrese.id
      ? { ...cartItem, quantity: cartItem.quantity - 1 }
      : cartItem
  )
}
const deleteCartItem = (cartItems, productToDelete) => {
  return cartItems.filter((cartItem) => cartItem.id !== productToDelete.id)
}

export const CartProvider = ({ children }) => {
  // const [isCartOpen, setIsCartOpen] = useState(false)
  // const [cartItems, setCartItems] = useState([])
  // const [cartCount, setCartCount] = useState(0)
  // const [cartTotal, setCartTotal] = useState(0)
  const [{ cartItems, isCartOpen, cartCount, cartTotal }, dispatch] =
    useReducer(cartReducer, INITIAL_STATE)

  // useEffect(() => {
  //   const newCartCount = cartItems.reduce(
  //     (total, cartItem) => total + cartItem.quantity,
  //     0
  //   )
  //   setCartCount(newCartCount)
  // }, [cartItems])

  // useEffect(() => {
  //   const newCartTotal = cartItems.reduce(
  //     (total, cartItem) => total + cartItem.quantity * cartItem.price,
  //     0
  //   )
  //   setCartTotal(newCartTotal)
  // }, [cartItems])
  const updateCartItemsReducer = (newCartItems) => {
    console.log(runn)

    const newCartCount = newCartItems.reduce(
      (total, cartItem) => total + cartItem.quantity,
      0
    )
    const newCartTotal = newCartItems.reduce(
      (total, cartItem) => total + cartItem.quantity * cartItem.price,
      0
    )
    dispatch({
      type: 'SET_CART_ITEMS',
      payload: {
        cartItems: newCartItems,
        cartTotal: newCartTotal,
        cartCount: newCartCount,
      },
    })
  }

  const addItemToCart = (productToAdd) => {
    const newCartItems = addCartItems(cartItems, productToAdd)
    updateCartItemsReducer(newCartItems)
  }
  const decreaseItemInCart = (productToDecrese) => {
    const newCartItems = decreaseCartItem(cartItems, productToDecrese)
    updateCartItemsReducer(newCartItems)
  }
  const deleteItemInCart = (productToDelete) => {
    const newCartItems = deleteCartItem(cartItems, productToDelete)
    updateCartItemsReducer(newCartItems)
  }

  const value = {
    isCartOpen,
    setIsCartOpen: () => true,
    addItemToCart,
    cartItems,
    cartTotal,
    setCartTotal: 0,
    cartCount,
    decreaseItemInCart,
    deleteItemInCart,
  }

  return <CartContext.Provider value={value}>{children}</CartContext.Provider>
}
