import { useState } from 'react'
import { useContext } from 'react'
import { CartContext } from '../../context/cart.context'
const CheckoutQuantity = ({ cartItem }) => {
  const { decreaseItemInCart } = useContext(CartContext)
  const decreaseQuantity = () => decreaseItemInCart(cartItem)

  return (
    <div>
      <button onClick={() => decreaseQuantity}>&lt; </button>
      <span>{cartItem.quantity}</span>
      <button>&gt;</button>
    </div>
  )
}

export default CheckoutQuantity
